import React, { useState } from 'react';
import Axios from 'axios'; // API 
import { Button, Flex, Heading, Input, useColorMode, useColorModeValue, Text } from '@chakra-ui/react';
import { useHistory } from 'react-router-dom';
import md5 from 'md5';


function UserRegistration() {
    const { toggleColorMode } = useColorMode(); /** Changes ColorMode */
    const formBackground = useColorModeValue("gray.100", "gray.700") /** Light Mode = gray.100,     Dark Mode = gray.700 */
    const [modeName, setModeName] = useState('Dark Mode'); /** Determines Light or Dark Mode Name */

    const handleModeName = () => { /** handles toggleColorMode & modeName */ 
        if (formBackground == "gray.100") {
            setModeName("Light Mode");
        } else {
            setModeName("Dark Mode");
        }
        toggleColorMode();
    } 

    const history = useHistory();

    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    let value = 0;

    function register() {
        let encryptedPassword = md5(password);
        setPassword(encryptedPassword);
        
        Axios.post('http://localhost:8080/api/v1/users', {
            firstName: firstName,
            lastName: lastName,
            email: email,
            password: encryptedPassword
        }).then((response) => {
            console.log(response.status, response, response.data);
            history.push(`/Login`);
        }).catch((err) => {
            console.log("Promise Rejected", err.message, err.response.data);
            alert (err.response.data.message);
        })
    }

    const handleFirstName = e => { 
        setFirstName(e.target.value)
    }

    const handleLastName = e => {
        setLastName(e.target.value)
    }

    const handleEmail = e => {
        setEmail(e.target.value);
    }

    const handlePassword = e => {
        setPassword(e.target.value);
    }

    return (
        <Flex height="100vh" alignItems="center" justifyContent="center">
            <Flex direction="column" background={formBackground} p={12} rounded={6}>
                <Heading mb={6}>Sign up
                <Button onClick={handleModeName} ml={6} size="xs">{modeName}</Button>
                </Heading>
                <Input type="text" value={firstName} onChange={handleFirstName} placeholder="first name" variant="filled" mb={3}></Input>
                <Input type="text" value={lastName} onChange={handleLastName} placeholder="last name" variant="filled" mb={3}></Input>
                <Input type="text" value={email} onChange={handleEmail} placeholder="email" variant="filled" mb={3}></Input>
                <Input type="password" value={password} onChange={handlePassword} placeholder="*********" variant="filled" mb={3}></Input>
                <Button onClick={register} mb={6} colorScheme="teal">Register</Button>
                <Text>Already have an account?</Text>
                <Button onClick={register} mb={6} size="md" colorScheme="red">Sign in</Button>
            </Flex>
        </Flex>
    )
}

export default UserRegistration;
