import React, { useState, useEffect } from 'react';
import tokenProvider from './TokenProvider';

const AuthProvider = {
    login: function(newTokens) {
        tokenProvider.setTokens(newTokens);
    },

    logout: function() {
        tokenProvider.setTokens(null);
    }
}

export default AuthProvider;
