import React, { useState } from 'react';
import { Button, Flex, Heading, Input, useColorMode, useColorModeValue} from '@chakra-ui/react';

function LoginPage() {
    const { toggleColorMode } = useColorMode();
    const { modeName, setModeName} = useState("Light Mode");
    const formBackground = useColorModeValue("gray.100", "gray.700")

    const handleModeName = () => {
        setModeName("Dark Mode");
    }

    const getModeName = () => {
        return modeName;
    }

    return (
        <Flex height="100vh" alignItems="center" justifyContent="center">
            <Flex direction="column" background={formBackground} p={12} rounded={6}>
                <Heading mb={6}>Log in</Heading>
                <Input type="text" placeholder="email" variant="filled" mb={3}></Input>
                <Input type="password" placeholder="*********" variant="filled" mb={6}></Input>
                <Button mb = {6} colorScheme="teal">Log in</Button>
                <Button onClick={toggleColorMode} onClick={handleModeName}>{getModeName}</Button>
            </Flex>
        </Flex>
    )
}

export default LoginPage
