import React, { Component } from "react";
import { Button, Flex, Heading, Input, useColorModeValue, Text, Alert, AlertIcon, AlertTitle, AlertDescription, CloseButton } from '@chakra-ui/react';

class EmailTakenAlert extends Component {
  render() {
    <Alert status="error">
      <AlertIcon />
      <AlertTitle mr={2}>Email taken</AlertTitle>
      <AlertDescription>The email is already taken</AlertDescription>
      <CloseButton position="absolute" right="6px" top="8px"/>
    </Alert>
  }

}


export default EmailTakenAlert
