import React, { useState, useEffect} from "react"
import { BrowserRouter, Router, Route, Switch, Redirect} from 'react-router-dom';
import LoginPage  from "./components/LoginPage.js";
import SignUpPage from "./components/SignUpPage.js";
import HomePage from "./components/HomePage.js";
import tokenProvider from './components/TokenProvider';
import StocksPage from "./components/StocksPage.js";
import authProvider from './components/AuthProvider';
import { useHistory } from 'react-router-dom';
require('dotenv').config()

function App() {
  const history = useHistory();
  const [logged, setLogged] = useState(false);
  useEffect(() => {
    handleLogged();
  }, [])

  function handleLogged() {
    if (authProvider.useAuth()) {
      console.log("Logged in: " + tokenProvider.isLoggedIn())
      console.log("Token expired: " + tokenProvider.isExpired(tokenProvider.getExpirationDate(localStorage.getItem("ACCESS_TOKEN"))));
      setLogged(true);
      history.push('/Dashboard');
    }
    else {
      console.log("Logged in: " + tokenProvider.isLoggedIn())
      console.log("Token expired: " + tokenProvider.isExpired(tokenProvider.getExpirationDate(localStorage.getItem("ACCESS_TOKEN"))));
      setLogged(false);
    }
  }
  return (
    <BrowserRouter>
      <Switch>

          <Route path="/Login" component={{logged} ? LoginPage : HomePage} />

          <Route path="/Register" exact component={SignUpPage}>
            <SignUpPage />
          </Route>

          <Route path="/Stocks" exact component={{logged} ? LoginPage : StocksPage} />

          <Route path="/Dashboard" exact component={{logged} ? LoginPage : HomePage} />

      </Switch>
    </BrowserRouter>
