import React, { useState, useEffect } from 'react';
import { Flex, Heading, useColorMode, useColorModeValue, Button, Box, Container, Grid, GridItem, extendTheme, withDefaultColorScheme} from '@chakra-ui/react';
import { useHistory } from 'react-router-dom';
import { DarkMode } from './DarkMode';
import tokenProvider from './TokenProvider';
import Sidebar from './Sidebar';
import authProvider from './AuthProvider';
import axios from 'axios'; // API 
import {
    Table,
    Thead,
    Tbody,
    Tfoot,
    Tr,
    Th,
    Td,
    TableCaption,
  } from "@chakra-ui/react"

function Home() {
    const { toggleColorMode } = useColorMode(); /** Changes ColorMode */
    const formBackground = useColorModeValue("gray.100", "gray.700") /** Light Mode = gray.100,     Dark Mode = gray.700 */
    const [modeName, setModeName] = useState('Dark Mode'); /** Determines Light or Dark Mode Name */
    const [data, setData] = useState('');
    const history = useHistory();  
    let email = 't';
    let firstName = '';
    let lastName = ''; 
    let balance = '';
    let positions = [];
    let test = '';

    useEffect(() => {
        let accessToken = tokenProvider.getAccessToken();
        axios.post('http://localhost:8080/api/v1/user/UserDetails?accessToken=' + accessToken, {
            accessToken: accessToken,
        }).then((response) => {  
            console.log(response.data);
            email = response.data.email;
            firstName = response.data.firstName;
            lastName = response.data.lastName;
        }).then(() => {
            axios.post('http://localhost:8080/api/v1/user/PositionDetails?accessToken=' + accessToken, {
                accessToken: accessToken,
            }).then((response) => {  
                console.log(response.data);
                balance = response.data.balance;
                test = response.data.positions;
                for (let i=0; i<response.data.positions.length; i++) {
                    const position = new Array(response.data.positions[i].symbol, "Shares: " + response.data.positions[i].shares, "Price " + response.data.positions[i].price_per_share);
                    positions[i] = position;
                }
            }).then(() => {
                setData(getData(email, firstName, lastName, balance, test)); //JSON.stringify(positions)
            }).catch((err) => {
                console.log(err);
            })
        }).catch((err) => {
            console.log(err);
        })
    }, []);

    function getData(email, firstName, lastName, balance, test) {
        return (
            <Container ml={5} mt={6}>
                <h1>Hello {firstName} {lastName},</h1>
                <h1>Email: {email}</h1>
                <h1>Balance: {balance}</h1>
                <h1>Portfolio: </h1>
                <h1>Positions {getTable(test)}</h1>
            </Container>
        )
    }

    function getTable(data) {
        let formattedData = '';
        console.log(data.length);
        for (let i=0; data.length; i++) {
            console.log(i);
            formattedData += 
                <Tr>
                    <Td>test</Td>
                    <Td>test</Td>
                    <Td>test</Td>
                </Tr>
        }
        return (
            <Table>
                <TableCaption>Top 5</TableCaption>
                <Thead>
                    <Tr>
                        <Th>Symbol</Th>
                        <Th>Shares</Th>
                        <Th>Price</Th>
                    </Tr>
                </Thead>
                <Tbody>
                    {formattedData}
                </Tbody>
            </Table>
        )
    }


    return (
        <Flex width="100%">
            <Sidebar />
            <Flex height="100vh" mt={6} display="block">
                <Heading ml={8}>Dashboard</Heading>
                {data}
            </Flex>
        </Flex>
    )
}

export default Home
