                    gap={4}
                    >
                        <GridItem rowSpan={2} colSpan={1} bg="papayawhip">
                        </GridItem>
                        <GridItem colSpan={3} bg="papayawhip">
                            {GME_GRAPH}
                        </GridItem>
                    </Grid>
                </Container>
            </Flex>
            */
export default StocksPage
