import React, { useState } from 'react';
import Axios from 'axios'; // API 
import { Button, Flex, Heading, Input, useColorModeValue, Text } from '@chakra-ui/react';
import { useHistory } from 'react-router-dom';
import { DarkMode } from './DarkMode';
import md5 from 'md5';

function LoginPage() {
    const formBackground = useColorModeValue("gray.100", "gray.700") /** Light Mode = gray.100,     Dark Mode = gray.700 */  
    const history = useHistory();

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    function LoginAuthentication() {
        let encryptedPassword = md5(password);
        setPassword(encryptedPassword);
        Axios.post('http://localhost:8080/api/auth/login', {
            email: email,
            password: encryptedPassword
        }).then((response) => {
            console.log(response.status, response, response.data);
            history.push(`/Home`);
        }).catch((err) => {
            console.log("Promise Rejected", err.message, err.response.data);
        })
    }

    const handleEmail = e => {
        setEmail(e.target.value);
    }

    const handlePassword = e => {
        setPassword(e.target.value);
    }

    return (
        <Flex height="100vh" alignItems="center" justifyContent="center">
            <Flex direction="column" background={formBackground} p={12} rounded={6}>
                <Heading mb={6}>Log in
                <DarkMode />
                </Heading>
                <Input type="text" placeholder="email" variant="filled" mb={3}></Input>
                <Input type="password" placeholder="*********" variant="filled" mb={6}></Input>
                <Button onClick={LoginAuthentication} mb={3} colorScheme="teal">Log in</Button>
                <Text>Dont have an account? Sign up!</Text>
                <Button onClick={() => history.push(`/Register`)} mb={6} colorScheme="red">Sign up</Button>
            </Flex>
        </Flex>
    )
}

export default LoginPage
